client/src/components/admin/announcement.content:
import { useState } from "react";
import { useAllAnnouncements, useCreateAnnouncement } from "@/lib/api";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Plus, Edit, Trash2, Megaphone } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export function Announcements() {
  const { data: announcements, isLoading } = useAllAnnouncements();
  const createAnnouncement = useCreateAnnouncement();
  const { toast } = useToast();
  const [dialogOpen, setDialogOpen] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    content: "",
    type: "News",
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.title.trim() || !formData.content.trim()) {
      toast({
        title: "Validation error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    createAnnouncement.mutate(formData, {
      onSuccess: () => {
        toast({
          title: "Announcement created",
          description: "Your announcement has been published successfully",
        });
        setDialogOpen(false);
        setFormData({ title: "", content: "", type: "News" });
      },
      onError: () => {
        toast({
          title: "Creation failed",
          description: "Failed to create announcement",
          variant: "destructive",
        });
      },
    });
  };

  const getBadgeVariant = (type: string) => {
    switch (type) {
      case "Important":
        return "destructive";
      case "Maintenance":
        return "secondary";
      case "Update":
        return "default";
      default:
        return "outline";
    }
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <Card>
          <CardHeader>
            <CardTitle>News & Announcements</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {Array.from({ length: 3 }).map((_, i) => (
                <div key={i} className="border rounded-lg p-4">
                  <div className="flex justify-between items-start mb-2">
                    <Skeleton className="h-5 w-48" />
                    <Skeleton className="h-4 w-20" />
                  </div>
                  <Skeleton className="h-4 w-full mb-2" />
                  <Skeleton className="h-4 w-3/4" />
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-1 h-full">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle>News & Announcements</CardTitle>
            <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
              <DialogTrigger asChild>
                <Button>
                  <Plus className="w-4 h-4 mr-2" />
                  New Announcement
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-2xl">
                <DialogHeader>
                  <DialogTitle>Create Announcement</DialogTitle>
                </DialogHeader>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div>
                    <Label htmlFor="title">Title</Label>
                    <Input
                      id="title"
                      value={formData.title}
                      onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                      placeholder="Announcement title"
                      required
                    />
                  </div>
                  <div>
                    <Label htmlFor="type">Type</Label>
                    <Select value={formData.type} onValueChange={(value) => setFormData({ ...formData, type: value })}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="News">News</SelectItem>
                        <SelectItem value="Important">Important</SelectItem>
                        <SelectItem value="Maintenance">Maintenance</SelectItem>
                        <SelectItem value="Update">Update</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div>
                    <Label htmlFor="content">Content</Label>
                    <Textarea
                      id="content"
                      rows={4}
                      value={formData.content}
                      onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                      placeholder="Announcement content"
                      required
                    />
                  </div>
                  <div className="flex justify-end space-x-3">
                    <Button type="button" variant="outline" onClick={() => setDialogOpen(false)}>
                      Cancel
                    </Button>
                    <Button type="submit" disabled={createAnnouncement.isPending}>
                      {createAnnouncement.isPending ? "Publishing..." : "Publish Announcement"}
                    </Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
        <CardContent>
          {!announcements || announcements.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Megaphone className="w-8 h-8 mx-auto mb-2 opacity-50" />
              <p>No announcements yet</p>
            </div>
          ) : (
            <div className="space-y-4">
              {announcements.map((announcement) => (
                <div key={announcement.id} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex justify-between items-start mb-2">
                    <h4 className="font-semibold text-gray-900">{announcement.title}</h4>
                    <div className="flex items-center space-x-2">
                      <span className="text-xs text-gray-500">
                        {new Date(announcement.createdAt).toLocaleDateString()}
                      </span>
                      <Badge variant={getBadgeVariant(announcement.type)}>
                        {announcement.type}
                      </Badge>
                    </div>
                  </div>
                  <p className="text-gray-600 text-sm mb-3">{announcement.content}</p>
                  <div className="flex justify-end space-x-2">
                    <Button variant="ghost" size="sm">
                      <Edit className="w-4 h-4 mr-1" />
                      Edit
                    </Button>
                    <Button variant="ghost" size="sm" className="text-destructive hover:text-destructive">
                      <Trash2 className="w-4 h-4 mr-1" />
                      Delete
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/admin/betting-orde.content:
import { useBettingOrders, useUpdateBettingOrder } from "@/lib/api";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { X, TrendingUp, TrendingDown, Volume2, VolumeX } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useNotificationSound } from "@/lib/notifications";
import { useEffect, useRef, useState } from "react";

const durations = [30, 60, 120, 180, 240];

export function BettingOrders() {
  const { data: orders, isLoading } = useBettingOrders();
  const updateOrder = useUpdateBettingOrder();
  const { toast } = useToast();
  const { playNewOrderSound, toggleNotifications, testSound, isEnabled } = useNotificationSound();
  const previousOrderCountRef = useRef<number>(0);

  // Track new active orders and play notification sound
  useEffect(() => {
    if (orders && Array.isArray(orders)) {
      const activeOrders = orders.filter(order => order.status === "active");
      const currentActiveOrderCount = activeOrders.length;
      console.log("Active orders count changed:", previousOrderCountRef.current, "->", currentActiveOrderCount);

      // Only play sound if we have more active orders than before (new orders added)
      if (previousOrderCountRef.current > 0 && currentActiveOrderCount > previousOrderCountRef.current) {
        console.log("New active orders detected, playing sound");
        playNewOrderSound();
        toast({
          title: "New Betting Order",
          description: `${currentActiveOrderCount - previousOrderCountRef.current} new order(s) received`,
        });
      }

      previousOrderCountRef.current = currentActiveOrderCount;
    }
  }, [orders, playNewOrderSound, toast]);

  // Add test sound function for debugging
  const handleTestSound = () => {
    console.log("Testing sound manually");
    testSound();
  };

  const handleUpdateDuration = (orderId: number, newDuration: number) => {
    const newExpiresAt = new Date(Date.now() + newDuration * 1000);

    updateOrder.mutate({ 
      id: orderId, 
      updates: { 
        duration: newDuration,
        expiresAt: newExpiresAt
      }
    }, {
      onSuccess: () => {
        toast({
          title: "Order updated",
          description: "Order duration has been updated successfully",
        });
      },
      onError: () => {
        toast({
          title: "Update failed",
          description: "Failed to update order duration",
          variant: "destructive",
        });
      },
    });
  };

  const handleCancelOrder = (orderId: number) => {
    updateOrder.mutate({ 
      id: orderId, 
      updates: { status: "cancelled" }
    }, {
      onSuccess: () => {
        toast({
          title: "Order cancelled",
          description: "Order has been cancelled successfully",
        });
      },
      onError: () => {
        toast({
          title: "Cancellation failed",
          description: "Failed to cancel order",
          variant: "destructive",
        });
      },
    });
  };

  const getRemainingTime = (expiresAt: Date) => {
    const now = new Date();
    const expiry = new Date(expiresAt);
    const remaining = Math.max(0, Math.floor((expiry.getTime() - now.getTime()) / 1000));
    return remaining;
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <Card>
          <CardHeader>
            <div className="flex justify-between items-center">
              <CardTitle>All Betting Orders</CardTitle>
              <Button
                variant="outline"
                size="sm"
                onClick={toggleNotifications}
                className="flex items-center gap-2"
              >
                {isEnabled ? <Volume2 className="w-4 h-4" /> : <VolumeX className="w-4 h-4" />}
                {isEnabled ? "Sound On" : "Sound Off"}
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {Array.from({ length: 5 }).map((_, i) => (
                <div key={i} className="flex items-center space-x-4">
                  <Skeleton className="h-4 w-20" />
                  <Skeleton className="h-4 w-32" />
                  <Skeleton className="h-4 w-24" />
                  <Skeleton className="h-4 w-16" />
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-1 h-full">
      <Card className="h-full">
        <CardHeader className="p-2">
          <div className="flex justify-between items-center mb-2">
            <CardTitle>All Betting Orders</CardTitle>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={toggleNotifications}
                className="flex items-center gap-2"
              >
                {isEnabled ? <Volume2 className="w-4 h-4" /> : <VolumeX className="w-4 h-4" />}
                {isEnabled ? "Sound On" : "Sound Off"}
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={handleTestSound}
                className="flex items-center gap-2"
              >
                Test Sound
              </Button>
            </div>
          </div>
          <div className="flex flex-wrap gap-2">
            <Button variant="default" size="sm">All Orders</Button>
            {durations.map((duration) => (
              <Button key={duration} variant="outline" size="sm">
                {duration}s
              </Button>
            ))}
          </div>
        </CardHeader>
        <CardContent>
          {!orders || orders.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <TrendingUp className="w-8 h-8 mx-auto mb-2 opacity-50" />
              <p>No betting orders</p>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Order ID</TableHead>
                  <TableHead>Customer Name</TableHead>
                  <TableHead>Asset</TableHead>
                  <TableHead>Amount</TableHead>
                  <TableHead>Direction</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Timer</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {orders.map((order) => {
                  const remainingTime = getRemainingTime(order.expiresAt);
                  const progressPercent = Math.max(0, (remainingTime / order.duration) * 100);

                  return (
                    <TableRow key={order.id}>
                      <TableCell className="font-medium">{order.id}-{(order as any).username || `User${order.userId}`}</TableCell>
                      <TableCell className="font-medium">{(order as any).username || `User${order.userId}`}</TableCell>
                      <TableCell>
                        <div>
                          <div className="font-medium">{order.asset}</div>
                          <div className="text-sm text-gray-500">{parseFloat(order.entryPrice).toFixed(2)}</div>
                        </div>
                      </TableCell>
                      <TableCell>{parseFloat(order.amount).toFixed(2)}</TableCell>
                      <TableCell>
                        <Badge 
                          variant={order.direction === "Buy Up" ? "default" : "destructive"}
                          className="flex items-center w-fit"
                        >
                          {order.direction === "Buy Up" ? (
                            <TrendingUp className="w-3 h-3 mr-1" />
                          ) : (
                            <TrendingDown className="w-3 h-3 mr-1" />
                          )}
                          {order.direction}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <Badge 
                          variant={order.status === "active" ? "default" : 
                                   order.status === "completed" ? "secondary" : "destructive"}
                        >
                          {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        {order.status === "active" ? (
                          <div>
                            <div className="text-sm font-medium text-warning">
                              {remainingTime}s
                            </div>
                            <div className="w-20 bg-gray-200 rounded-full h-1 mt-1">
                              <div 
                                className="bg-warning h-1 rounded-full transition-all duration-1000"
                                style={{ width: `${progressPercent}%` }}
                              />
                            </div>
                          </div>
                        ) : (
                          <div className="text-sm text-gray-500">
                            {order.status === "completed" ? "Completed" : "Cancelled"}
                          </div>
                        )}
                      </TableCell>
                      <TableCell>
                        {order.status === "active" ? (
                          <div className="flex space-x-2">
                            <Select 
                              value={order.duration.toString()} 
                              onValueChange={(value) => handleUpdateDuration(order.id, parseInt(value))}
                            >
                              <SelectTrigger className="w-20">
                                <SelectValue />
                              </SelectTrigger>
                              <SelectContent>
                                {durations.map((duration) => (
                                  <SelectItem key={duration} value={duration.toString()}>
                                    {duration}s
                                  </SelectItem>
                                ))}
                              </SelectContent>
                            </Select>
                            <Button
                              variant="destructive"
                              size="sm"
                              onClick={() => handleCancelOrder(order.id)}
                            >
                              <X className="w-4 h-4" />
                            </Button>
                          </div>
                        ) : (
                          <div className="text-sm text-gray-500">
                            {order.status === "completed" && order.profit && (
                              <div>
                                Profit: {parseFloat(order.profit).toFixed(2)}
                              </div>
                            )}
                          </div>
                        )}
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

