import { useState, useEffect } from "react";
import { useUsers, useUpdateUser, useCreateUser, useCreateTransaction, useTransactions, useUpdateTransaction, useDeleteUser, useCreateMessage } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Switch } from "@/components/ui/switch";
import { Edit, Wallet, Lock, Eye, Plus, Minus, LockOpen, UserPlus, Settings, Ban, CheckCircle, XCircle, AlertTriangle, Unlock, Trash2, Send, Key, FileText } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { User } from "@shared/schema";

export function MemberManagement() {
  const { data: users, isLoading } = useUsers();
  const { data: transactions } = useTransactions();
  const updateUser = useUpdateUser();
  const createUser = useCreateUser();
  const createTransaction = useCreateTransaction();
  const updateTransaction = useUpdateTransaction();
  const deleteUser = useDeleteUser();
  const createMessage = useCreateMessage();
  const { toast } = useToast();

  // Auto-refresh user data every 2 seconds for real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/betting-orders"] });
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [detailsDialogOpen, setDetailsDialogOpen] = useState(false);
  const [depositDialogOpen, setDepositDialogOpen] = useState(false);
  const [deductionDialogOpen, setDeductionDialogOpen] = useState(false);
  const [orderDialogOpen, setOrderDialogOpen] = useState(false);
  const [messageDialogOpen, setMessageDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [passwordDialogOpen, setPasswordDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [depositAmount, setDepositAmount] = useState("");
  const [deductionAmount, setDeductionAmount] = useState("");
  const [messageTitle, setMessageTitle] = useState("");
  const [messageContent, setMessageContent] = useState("");
  const [freezeDialogOpen, setFreezeDialogOpen] = useState(false);
  const [freezeAmount, setFreezeAmount] = useState("");
  const [unfreezeDialogOpen, setUnfreezeDialogOpen] = useState(false);
  const [unfreezeAmount, setUnfreezeAmount] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [otherDialogOpen, setOtherDialogOpen] = useState(false);
  const [creditScore, setCreditScore] = useState("");
  const [addMemberDialogOpen, setAddMemberDialogOpen] = useState(false);
  const [newMemberData, setNewMemberData] = useState({
    username: "",
    email: "",
    password: "",
    name: "",
    reputation: 100,
    creditScore: 100
  });

  // Filter and sort users based on search term, ID descending (newest first)
  const filteredUsers = users?.filter(user => 
    user.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.username?.toLowerCase().includes(searchTerm.toLowerCase())
  ).sort((a, b) => b.id - a.id) || [];

  const handleQuickUpdate = (user: User, updates: Partial<User>) => {
    updateUser.mutate(
      { id: user.id, updates },
      {
        onSuccess: () => {
          toast({ title: "User updated successfully" });
          // Immediately refresh the data for real-time updates
          queryClient.invalidateQueries({ queryKey: ["/api/users"] });
          queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
        },
        onError: () => {
          toast({ title: "Failed to update user", variant: "destructive" });
        },
      }
    );
  };

  const handleFreezeAmount = (user: User, amount: number) => {
    const currentAvailable = parseFloat(user.availableBalance || "0");
    const currentFrozen = parseFloat(user.frozenBalance || "0");

    if (amount > currentAvailable) {
      toast({ title: "Insufficient available balance", variant: "destructive" });
      return;
    }

    const newAvailable = currentAvailable - amount;
    const newFrozen = currentFrozen + amount;

    handleQuickUpdate(user, {
      availableBalance: newAvailable.toString(),
      frozenBalance: newFrozen.toString(),
    });
    setFreezeAmount("");
    setFreezeDialogOpen(false);
  };

  const handleUnfreezeAmount = (user: User, amount: number) => {
    const currentAvailable = parseFloat(user.availableBalance || "0");
    const currentFrozen = parseFloat(user.frozenBalance || "0");

    if (amount > currentFrozen) {
      toast({ title: "Insufficient frozen balance", variant: "destructive" });
      return;
    }

    const newAvailable = currentAvailable + amount;
    const newFrozen = currentFrozen - amount;

    handleQuickUpdate(user, {
      availableBalance: newAvailable.toString(),
      frozenBalance: newFrozen.toString(),
    });
    setUnfreezeAmount("");
    setUnfreezeDialogOpen(false);
  };

  const handleDepositWithdraw = (type: "deposit" | "withdraw", amount: number) => {
    if (!selectedUser || amount <= 0) return;

    const currentBalance = parseFloat(selectedUser.balance || "0");
    const currentAvailable = parseFloat(selectedUser.availableBalance || "0");

    let newBalance, newAvailable;

    if (type === "deposit") {
      newBalance = currentBalance + amount;
      newAvailable = currentAvailable + amount;
    } else {
      if (amount > currentAvailable) {
        toast({ title: "Insufficient available balance", variant: "destructive" });
        return;
      }
      newBalance = currentBalance - amount;
      newAvailable = currentAvailable - amount;
    }

    handleQuickUpdate(selectedUser, {
      balance: newBalance.toString(),
      availableBalance: newAvailable.toString(),
    });

    // Reset states
    setDepositAmount("");
    setDeductionAmount("");
    setDepositDialogOpen(false);
    setDeductionDialogOpen(false);
  };

  const handleDeleteUser = (user: User) => {
    deleteUser.mutate(user.id, {
      onSuccess: () => {
        toast({ title: "User deleted successfully" });
        setDeleteDialogOpen(false);
        setSelectedUser(null);
        // Immediately refresh the data for real-time updates
        queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      },
      onError: () => {
        toast({ title: "Failed to delete user", variant: "destructive" });
      },
    });
  };

  const handleSendMessage = () => {
    if (!selectedUser || !messageTitle || !messageContent) return;

    createMessage.mutate({
      recipientId: selectedUser.id,
      title: messageTitle,
      content: messageContent,
    }, {
      onSuccess: () => {
        toast({ title: "Message sent successfully" });
        setMessageTitle("");
        setMessageContent("");
        setMessageDialogOpen(false);
        // Immediately refresh the data for real-time updates
        queryClient.invalidateQueries({ queryKey: ["/api/messages"] });
      },
      onError: () => {
        toast({ title: "Failed to send message", variant: "destructive" });
      },
    });
  };

  const handleAddMember = () => {
    createUser.mutate({
      ...newMemberData,
      role: "customer",
      availableBalance: "0.00",
      frozenBalance: "0.00",
      invitationCode: `100${String(Date.now()).slice(-3)}`,
      userType: "Normal",
      generalAgent: "Agent001",
      remark: "New Member",
      creditScore: newMemberData.creditScore || 100,
    }, {
      onSuccess: () => {
        toast({ title: "Member added successfully" });
        setAddMemberDialogOpen(false);
        setNewMemberData({
          username: "",
          email: "",
          password: "",
          name: "",
          reputation: 100,
          creditScore: 100
        });
        // Immediately refresh the data for real-time updates
        queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      },
      onError: () => {
        toast({ title: "Failed to add member", variant: "destructive" });
      },
    });
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <Skeleton className="h-8 w-64" />
        <Skeleton className="h-64 w-full" />
      </div>
    );
  }

  return (
    <div className="h-full">
      <Card className="h-full">
        <CardHeader className="p-6">
          <CardTitle>Member Management</CardTitle>
          <div className="flex gap-1 justify-between">
            <Input
              placeholder="Search users..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="max-w-sm"
            />
            <Dialog open={addMemberDialogOpen} onOpenChange={setAddMemberDialogOpen}>
              <DialogTrigger asChild>
                <Button className="bg-blue-600 hover:bg-blue-700">
                  <UserPlus className="w-4 h-4 mr-2" />
                  Add New Member
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-md">
                <DialogHeader>
                  <DialogTitle>Add New Member</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <Label>Username</Label>
                    <Input 
                      value={newMemberData.username}
                      onChange={(e) => setNewMemberData(prev => ({ ...prev, username: e.target.value }))}
                      placeholder="Enter username"
                    />
                  </div>
                  <div>
                    <Label>Email</Label>
                    <Input 
                      type="email"
                      value={newMemberData.email}
                      onChange={(e) => setNewMemberData(prev => ({ ...prev, email: e.target.value }))}
                      placeholder="Enter email"
                    />
                  </div>
                  <div>
                    <Label>Password</Label>
                    <Input 
                      type="password"
                      value={newMemberData.password}
                      onChange={(e) => setNewMemberData(prev => ({ ...prev, password: e.target.value }))}
                      placeholder="Enter password"
                    />
                  </div>
                  <div>
                    <Label>Full Name</Label>
                    <Input 
                      value={newMemberData.name}
                      onChange={(e) => setNewMemberData(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Enter full name"
                    />
                  </div>
                  <div>
                    <Label>VIP Level (Reputation)</Label>
                    <Input 
                      type="number"
                      value={newMemberData.reputation}
                      onChange={(e) => setNewMemberData(prev => ({ ...prev, reputation: parseInt(e.target.value) || 100 }))}
                      placeholder="Enter reputation (0-100)"
                      min="0"
                      max="100"
                    />
                  </div>
                  <div className="flex justify-end gap-2">
                    <Button 
                      variant="outline" 
                      onClick={() => {
                        setAddMemberDialogOpen(false);
                        setNewMemberData({
                          username: "",
                          email: "",
                          password: "",
                          name: "",
                          reputation: 100,
                          creditScore: 100
                        });
                      }}
                    >
                      Cancel
                    </Button>
                    <Button 
                      onClick={handleAddMember}
                      disabled={!newMemberData.username || !newMemberData.email || !newMemberData.password || !newMemberData.name}
                    >
                      Add Member
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
        <CardContent className="p-2">
          <div className="overflow-x-auto">
            <Table className="min-w-[1660px] table-fixed">
              <TableHeader>
                <TableRow>
                  <TableHead className="w-[50px] text-center">ID</TableHead>
                  <TableHead className="w-[100px]">Username</TableHead>
                  <TableHead className="w-[180px]">Balance</TableHead>
                  <TableHead className="w-[80px] text-center">Credit Score</TableHead>
                  <TableHead className="w-[100px] text-center">General Agent</TableHead>
                  <TableHead className="w-[100px] text-center">Invitation Code</TableHead>
                  <TableHead className="w-[80px] text-center">Type</TableHead>
                  <TableHead className="w-[90px] text-center">Direction</TableHead>
                  <TableHead className="w-[60px] text-center">Ban</TableHead>
                  <TableHead className="w-[80px] text-center">Withdraw</TableHead>
                  <TableHead className="w-[110px] text-center">Registration Time</TableHead>
                  <TableHead className="w-[100px] text-center">Remark</TableHead>
                  <TableHead className="w-[500px] text-center">Operate</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredUsers.map((user) => (
                  <TableRow key={user.id}>
                    <TableCell className="font-mono text-sm text-center w-[50px]">{user.id}</TableCell>
                    <TableCell className="font-medium w-[100px] truncate">{user.username}</TableCell>
                    <TableCell className="w-[180px]">
                      <div className="space-y-1 text-xs">
                        <div>
                          Total: {(parseFloat(user.availableBalance || "0") + parseFloat(user.frozenBalance || "0")).toFixed(2)}
                        </div>
                        <div className="text-gray-600">
                          Available: {parseFloat(user.availableBalance || "0").toFixed(2)}
                        </div>
                        <div className="text-gray-600">
                          Frozen: {parseFloat(user.frozenBalance || "0").toFixed(2)}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell className="text-center w-[80px]">
                      <Badge variant="outline" className="text-xs px-1 py-0.5">
                        {user.creditScore || 100}
                      </Badge>
                    </TableCell>
                    <TableCell className="text-xs text-center w-[100px] truncate">{user.generalAgent || "admin"}</TableCell>
                    <TableCell className="text-xs text-center w-[100px] truncate">{user.invitationCode || "100025"}</TableCell>
                    <TableCell className="w-[80px]">
                      <Select
                        value={user.userType || "Normal"}
                        onValueChange={(value) => handleQuickUpdate(user, { userType: value })}
                      >
                        <SelectTrigger className="w-full h-7 text-xs">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="Normal">Normal</SelectItem>
                          <SelectItem value="VIP">VIP</SelectItem>
                          <SelectItem value="Agent">Agent</SelectItem>
                        </SelectContent>
                      </Select>
                    </TableCell>
                    <TableCell className="w-[90px]">
                      <Select
                        value={user.direction || "Actual"}
                        onValueChange={(value) => handleQuickUpdate(user, { direction: value })}
                      >
                        <SelectTrigger className="w-full h-7 text-xs">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="Buy Up">Buy Up</SelectItem>
                          <SelectItem value="Buy Down">Buy Down</SelectItem>
                          <SelectItem value="Actual">Actual</SelectItem>
                        </SelectContent>
                      </Select>
                    </TableCell>
                    <TableCell className="text-center w-[60px]">
                      <Switch
                        checked={user.isBanned || false}
                        onCheckedChange={(checked) => handleQuickUpdate(user, { isBanned: checked })}
                        className="scale-75"
                      />
                    </TableCell>
                    <TableCell className="text-center w-[80px]">
                      <Switch
                        checked={user.withdrawalProhibited || false}
                        onCheckedChange={(checked) => handleQuickUpdate(user, { withdrawalProhibited: checked })}
                        className="scale-75"
                      />
                    </TableCell>
                    <TableCell className="w-[110px]">
                      <div className="text-xs text-center">
                        {user.registrationTime ? 
                          new Date(user.registrationTime).toLocaleDateString('en-GB') : 
                          new Date().toLocaleDateString('en-GB')
                        }
                      </div>
                    </TableCell>
                    <TableCell className="w-[100px]">
                      <Input
                        value={user.remark || ""}
                        onChange={(e) => handleQuickUpdate(user, { remark: e.target.value })}
                        className="w-full h-7 text-xs"
                        placeholder="Remark"
                      />
                    </TableCell>
                    <TableCell className="w-[500px]">
                      <div className="flex flex-wrap gap-1 justify-start">
                        {/* Password Management Button */}
                        <Dialog open={passwordDialogOpen && selectedUser?.id === user.id} onOpenChange={setPasswordDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-blue-50 text-blue-600 border-blue-200 hover:bg-blue-100"
                              onClick={() => setSelectedUser(user)}
                            >
                              <Key className="w-3 h-3 mr-1" />
                              Confidential
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-md">
                            <DialogHeader>
                              <DialogTitle>Password Management</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div className="space-y-4">
                                <div>
                                  <Label htmlFor="new-password">New Password</Label>
                                  <Input
                                    id="new-password"
                                    type="password"
                                    value={newPassword}
                                    onChange={(e) => setNewPassword(e.target.value)}
                                    placeholder="Enter new password"
                                  />
                                </div>
                                <div>
                                  <Label htmlFor="confirm-password">Confirm Password</Label>
                                  <Input
                                    id="confirm-password"
                                    type="password"
                                    value={confirmPassword}
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                    placeholder="Confirm new password"
                                  />
                                </div>
                                <div className="flex gap-2 justify-end">
                                  <Button
                                    variant="outline"
                                    onClick={() => {
                                      setPasswordDialogOpen(false);
                                      setNewPassword("");
                                      setConfirmPassword("");
                                    }}
                                  >
                                    Cancel
                                  </Button>
                                  <Button
                                    onClick={() => {
                                      if (newPassword !== confirmPassword) {
                                        toast({ title: "Passwords do not match", variant: "destructive" });
                                        return;
                                      }
                                      if (newPassword.length < 4) {
                                        toast({ title: "Password must be at least 4 characters", variant: "destructive" });
                                        return;
                                      }
                                      if (selectedUser) {
                                        handleQuickUpdate(selectedUser, { password: newPassword });
                                        setPasswordDialogOpen(false);
                                        setNewPassword("");
                                        setConfirmPassword("");
                                        toast({ title: "Password updated successfully" });
                                      }
                                    }}
                                    disabled={!newPassword || !confirmPassword}
                                  >
                                    Update Password
                                  </Button>
                                </div>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>

                        {/* Details Button */}
                        <Dialog open={detailsDialogOpen && selectedUser?.id === user.id} onOpenChange={setDetailsDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-green-50 text-green-600 border-green-200 hover:bg-green-100"
                              onClick={() => setSelectedUser(user)}
                            >
                              <CheckCircle className="w-3 h-3 mr-1" />
                              Details
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-2xl">
                            <DialogHeader>
                              <DialogTitle>User Details: {selectedUser?.name}</DialogTitle>
                            </DialogHeader>
                            {selectedUser && (
                              <div className="grid grid-cols-2 gap-4">
                                <div className="space-y-3">
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">User ID</Label>
                                    <p className="text-sm">{selectedUser.id}</p>
                                  </div>
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Username</Label>
                                    <p className="text-sm">{selectedUser.username}</p>
                                  </div>
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Email</Label>
                                    <p className="text-sm">{selectedUser.email}</p>
                                  </div>
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Name</Label>
                                    <p className="text-sm">{selectedUser.name}</p>
                                  </div>
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Role</Label>
                                    <Badge>{selectedUser.role}</Badge>
                                  </div>
                                </div>
                                <div className="space-y-3">
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Total Balance</Label>
                                    <p className="text-sm font-semibold">{(parseFloat(selectedUser.availableBalance || "0") + parseFloat(selectedUser.frozenBalance || "0")).toFixed(2)}</p>
                                  </div>
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Available Balance</Label>
                                    <p className="text-sm">{parseFloat(selectedUser.availableBalance || "0").toFixed(2)}</p>
                                  </div>
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Frozen Balance</Label>
                                    <p className="text-sm">{parseFloat(selectedUser.frozenBalance || "0").toFixed(2)}</p>
                                  </div>
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Reputation</Label>
                                    <p className="text-sm">{selectedUser.reputation}/100</p>
                                  </div>
                                  <div>
                                    <Label className="text-sm font-medium text-gray-600">Account Status</Label>
                                    <Badge variant={selectedUser.isActive ? "default" : "destructive"}>
                                      {selectedUser.isActive ? "Active" : "Inactive"}
                                    </Badge>
                                  </div>
                                </div>
                              </div>
                            )}
                          </DialogContent>
                        </Dialog>



                        {/* Deposit Button */}
                        <Dialog open={depositDialogOpen && selectedUser?.id === user.id} onOpenChange={setDepositDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-orange-50 text-orange-600 border-orange-200 hover:bg-orange-100"
                              onClick={() => setSelectedUser(user)}
                            >
                              <Plus className="w-3 h-3 mr-1" />
                              Deposit
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-md">
                            <DialogHeader>
                              <DialogTitle>Deposit Funds</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <Label>Current Balance: {(parseFloat(user.availableBalance || "0") + parseFloat(user.frozenBalance || "0")).toFixed(2)}</Label>
                              </div>
                              <div>
                                <Label>Deposit Amount</Label>
                                <Input 
                                  type="number"
                                  value={depositAmount}
                                  onChange={(e) => setDepositAmount(e.target.value)}
                                  placeholder="Enter amount to deposit"
                                />
                              </div>
                              <div className="flex justify-end gap-2">
                                <Button variant="outline" onClick={() => setDepositDialogOpen(false)}>
                                  Cancel
                                </Button>
                                <Button onClick={() => handleDepositWithdraw("deposit", parseFloat(depositAmount))} disabled={!depositAmount}>
                                  Deposit {depositAmount || "0"}
                                </Button>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>

                        {/* Deduction Button */}
                        <Dialog open={deductionDialogOpen && selectedUser?.id === user.id} onOpenChange={setDeductionDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-red-50 text-red-600 border-red-200 hover:bg-red-100"
                              onClick={() => setSelectedUser(user)}
                            >
                              <Minus className="w-3 h-3 mr-1" />
                              Deduction
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-md">
                            <DialogHeader>
                              <DialogTitle>Deduct Funds</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <Label>Current Balance: {(parseFloat(user.availableBalance || "0") + parseFloat(user.frozenBalance || "0")).toFixed(2)}</Label>
                              </div>
                              <div>
                                <Label>Deduction Amount</Label>
                                <Input 
                                  type="number"
                                  value={deductionAmount}
                                  onChange={(e) => setDeductionAmount(e.target.value)}
                                  placeholder="Enter amount to deduct"
                                />
                              </div>
                              <div className="flex justify-end gap-2">
                                <Button variant="outline" onClick={() => setDeductionDialogOpen(false)}>
                                  Cancel
                                </Button>
                                <Button onClick={() => handleDepositWithdraw("withdraw", parseFloat(deductionAmount))} disabled={!deductionAmount} variant="destructive">
                                  Deduct {deductionAmount || "0"}
                                </Button>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>



                        {/* Freeze Button */}
                        <Dialog open={freezeDialogOpen && selectedUser?.id === user.id} onOpenChange={setFreezeDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-blue-50 text-blue-600 border-blue-200 hover:bg-blue-100"
                              onClick={() => setSelectedUser(user)}
                            >
                              <Lock className="w-3 h-3 mr-1" />
                              Freeze
                            </Button>
                          </DialogTrigger>
                          <DialogContent>
                            <DialogHeader>
                              <DialogTitle>Freeze Balance</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <Label htmlFor="freezeAmount">Amount to Freeze</Label>
                                <Input
                                  id="freezeAmount"
                                  type="number"
                                  placeholder="Enter amount to freeze"
                                  value={freezeAmount}
                                  onChange={(e) => setFreezeAmount(e.target.value)}
                                />
                                <p className="text-sm text-muted-foreground mt-1">
                                  Available Balance: {user.availableBalance}
                                </p>
                              </div>
                              <div className="flex justify-end gap-2">
                                <Button variant="outline" onClick={() => setFreezeDialogOpen(false)}>
                                  Cancel
                                </Button>
                                <Button 
                                  onClick={() => handleFreezeAmount(user, parseFloat(freezeAmount))} 
                                  className="bg-blue-600 hover:bg-blue-700"
                                >
                                  Freeze Amount
                                </Button>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>

                        {/* Unfreeze Button */}
                        <Dialog open={unfreezeDialogOpen && selectedUser?.id === user.id} onOpenChange={setUnfreezeDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-green-50 text-green-600 border-green-200 hover:bg-green-100"
                              onClick={() => setSelectedUser(user)}
                            >
                              <Unlock className="w-3 h-3 mr-1" />
                              Unfreeze
                            </Button>
                          </DialogTrigger>
                          <DialogContent>
                            <DialogHeader>
                              <DialogTitle>Unfreeze Balance</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <Label htmlFor="unfreezeAmount">Amount to Unfreeze</Label>
                                <Input
                                  id="unfreezeAmount"
                                  type="number"
                                  placeholder="Enter amount to unfreeze"
                                  value={unfreezeAmount}
                                  onChange={(e) => setUnfreezeAmount(e.target.value)}
                                />
                                <p className="text-sm text-muted-foreground mt-1">
                                  Frozen Balance: {user.frozenBalance}
                                </p>
                              </div>
                              <div className="flex justify-end gap-2">
                                <Button variant="outline" onClick={() => setUnfreezeDialogOpen(false)}>
                                  Cancel
                                </Button>
                                <Button onClick={() => handleUnfreezeAmount(user, parseFloat(unfreezeAmount))} className="bg-green-600 hover:bg-green-700">
                                  Unfreeze Amount
                                </Button>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>

                        {/* Send Message Button */}
                        <Dialog open={messageDialogOpen && selectedUser?.id === user.id} onOpenChange={setMessageDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-yellow-50 text-yellow-600 border-yellow-200 hover:bg-yellow-100"
                              onClick={() => setSelectedUser(user)}
                            >
                              <Send className="w-3 h-3 mr-1" />
                              Send a letter
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-md">
                            <DialogHeader>
                              <DialogTitle>Send Message to {selectedUser?.name}</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <Label>Message Title</Label>
                                <Input 
                                  value={messageTitle}
                                  onChange={(e) => setMessageTitle(e.target.value)}
                                  placeholder="Enter message title"
                                />
                              </div>
                              <div>
                                <Label>Message Content</Label>
                                <Textarea 
                                  value={messageContent}
                                  onChange={(e) => setMessageContent(e.target.value)}
                                  placeholder="Enter your message here..."
                                  rows={4}
                                />
                              </div>
                              <div className="flex justify-end gap-2">
                                <Button variant="outline" onClick={() => setMessageDialogOpen(false)}>
                                  Cancel
                                </Button>
                                <Button onClick={handleSendMessage} disabled={!messageTitle || !messageContent}>
                                  Send Message
                                </Button>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>

                        {/* Other Button */}
                        <Dialog open={otherDialogOpen && selectedUser?.id === user.id} onOpenChange={setOtherDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-purple-50 text-purple-600 border-purple-200 hover:bg-purple-100"
                              onClick={() => {
                                setSelectedUser(user);
                                setCreditScore(user.reputation?.toString() || "100");
                              }}
                            >
                              <Settings className="w-3 h-3 mr-1" />
                              Other
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-md">
                            <DialogHeader>
                              <DialogTitle>Other Settings for {selectedUser?.name}</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <Label>Credit Score</Label>
                                <Input
                                  type="number"
                                  value={creditScore || selectedUser?.creditScore || 100}
                                  onChange={(e) => setCreditScore(e.target.value)}
                                  min="0"
                                  max="100"
                                  placeholder="Credit Score (0-100)"
                                />
                              </div>
                              <div className="flex justify-end gap-2">
                                <Button variant="outline" onClick={() => {
                                  setOtherDialogOpen(false);
                                  setCreditScore("");
                                }}>
                                  Cancel
                                </Button>
                                <Button onClick={() => {
                                  const newScore = parseInt(creditScore);
                                  if (newScore >= 0 && newScore <= 100 && selectedUser) {
                                    handleQuickUpdate(selectedUser, { creditScore: newScore });
                                    setOtherDialogOpen(false);
                                    setCreditScore("");
                                    toast({ title: "Credit score updated successfully" });
                                  } else {
                                    toast({ title: "Please enter a valid score (0-100)", variant: "destructive" });
                                  }
                                }}>
                                  Save
                                </Button>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>

                        {/* Delete Button */}
                        <Dialog open={deleteDialogOpen && selectedUser?.id === user.id} onOpenChange={setDeleteDialogOpen}>
                          <DialogTrigger asChild>
                            <Button 
                              size="sm" 
                              variant="outline" 
                              className="h-6 px-2 text-xs bg-red-50 text-red-600 border-red-200 hover:bg-red-100"
                              onClick={() => setSelectedUser(user)}
                            >
                              <Trash2 className="w-3 h-3 mr-1" />
                              Delete
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-md">
                            <DialogHeader>
                              <DialogTitle>Delete User</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div className="text-center py-4">
                                <AlertTriangle className="w-12 h-12 mx-auto mb-2 text-red-500" />
                                <p className="text-lg font-semibold">Are you sure?</p>
                                <p className="text-sm text-gray-600">
                                  This will permanently delete user "{selectedUser?.name}" and all associated data.
                                </p>
                                <p className="text-sm text-red-600 font-medium">
                                  This action cannot be undone.
                                </p>
                              </div>
                              <div className="flex justify-end gap-2">
                                <Button variant="outline" onClick={() => setDeleteDialogOpen(false)}>
                                  Cancel
                                </Button>
                                <Button 
                                  onClick={() => selectedUser && handleDeleteUser(selectedUser)}
                                  variant="destructive"
                                >
                                  Delete User
                                </Button>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import { useState } from "react";
import { useUsers, useUpdateUser, useCreateTransaction } from "@/lib/api";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Switch } from "@/components/ui/switch";
import { Edit, Wallet, Lock, Eye, Plus, Minus, LockOpen, UserPlus, Settings, Ban, CheckCircle, XCircle, AlertTriangle, ChevronLeft, ChevronRight } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { User } from "@shared/schema";

export function MemberManagement() {
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  const limit = 25;

  const { data: response, isLoading } = useUsers(currentPage, limit, searchTerm);
  const updateUser = useUpdateUser();
  const createTransaction = useCreateTransaction();
  const { toast } = useToast();
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);

  const users = response?.users || [];
  const pagination = response?.pagination || {
    currentPage: 1,
    totalPages: 1,
    totalCount: 0,
    hasNextPage: false,
    hasPrevPage: false
  };

  const filteredUsers = users.filter((user: any) => user.role === "customer");

  // Handle search term changes
  const handleSearchChange = (value: string) => {
    setSearchTerm(value);
    setCurrentPage(1); // Reset to first page when searching
  };

  // Pagination handlers
  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  const handleUpdateUser = (updates: Partial<User>) => {
    if (!selectedUser) return;

    updateUser.mutate({ id: selectedUser.id, updates }, {
      onSuccess: () => {
        toast({
          title: "User updated",
          description: "User information has been updated successfully",
        });
        setEditDialogOpen(false);
      },
      onError: () => {
        toast({
          title: "Update failed",
          description: "Failed to update user information",
          variant: "destructive",
        });
      },
    });
  };

  const handleBalanceAction = (action: string, amount: string) => {
    if (!selectedUser || !amount || parseFloat(amount) <= 0) return;

    createTransaction.mutate({
      userId: selectedUser.id,
      type: action,
      amount,
      description: `Admin ${action}: ${amount}`,
    }, {
      onSuccess: () => {
        toast({
          title: "Transaction completed",
          description: `${action} of ${amount} completed successfully`,
        });
      },
      onError: () => {
        toast({
          title: "Transaction failed",
          description: `Failed to process ${action}`,
          variant: "destructive",
        });
      },
    });
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <Card>
          <CardHeader>
            <CardTitle>Customer List</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {Array.from({ length: 5 }).map((_, i) => (
                <div key={i} className="flex items-center space-x-4">
                  <Skeleton className="h-12 w-12 rounded-full" />
                  <div className="space-y-2">
                    <Skeleton className="h-4 w-[200px]" />
                    <Skeleton className="h-4 w-[150px]" />
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-6">
      <Card>
        <CardHeader>
          <CardTitle>Customer List</CardTitle>
          <div className="flex gap-4">
            <Input
              placeholder="Search customers..."
              value={searchTerm}
              onChange={(e) => handleSearchChange(e.target.value)}
              className="max-w-sm"
            />
          </div>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Customer</TableHead>
                <TableHead>Balance</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredUsers.map((user: any) => (
                <TableRow key={user.id}>
                  <TableCell>
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 rounded-full bg-primary text-white flex items-center justify-center text-sm font-medium">
                        {user.name.charAt(0)}
                      </div>
                      <div>
                        <div className="font-medium">{user.name}</div>
                        <div className="text-sm text-gray-500">{user.email}</div>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div>
                      <div className="font-medium">{parseFloat(user.balance).toFixed(2)}</div>
                      <div className="text-sm text-gray-500">
                        Available: {parseFloat(user.availableBalance).toFixed(2)}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={user.isBanned ? "destructive" : "default"}>
                      {user.isBanned ? "Banned" : "Active"}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <div className="flex space-x-2">
                      <Dialog open={editDialogOpen && selectedUser?.id === user.id} onOpenChange={setEditDialogOpen}>
                        <DialogTrigger asChild>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => setSelectedUser(user)}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                        </DialogTrigger>
                        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                          <DialogHeader>
                            <DialogTitle>Edit Customer: {user.name}</DialogTitle>
                          </DialogHeader>
                          {selectedUser && <CustomerEditForm user={selectedUser} onUpdate={handleUpdateUser} onBalanceAction={handleBalanceAction} />}
                        </DialogContent>
                      </Dialog>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>

          {/* Pagination */}
          <div className="flex items-center justify-between pt-4">
            <div className="text-sm text-gray-500">
              Showing {((currentPage - 1) * limit) + 1} to {Math.min(currentPage * limit, pagination.totalCount)} of {pagination.totalCount} customers
            </div>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={!pagination.hasPrevPage}
              >
                <ChevronLeft className="w-4 h-4" />
                Previous
              </Button>

              <div className="flex items-center gap-1">
                {Array.from({ length: Math.min(5, pagination.totalPages) }, (_, i) => {
                  const pageNum = currentPage <= 3 
                    ? i + 1 
                    : currentPage >= pagination.totalPages - 2
                    ? pagination.totalPages - 4 + i
                    : currentPage - 2 + i;

                  if (pageNum > pagination.totalPages || pageNum < 1) return null;

                  return (
                    <Button
                      key={pageNum}
                      variant={pageNum === currentPage ? "default" : "outline"}
                      size="sm"
                      onClick={() => handlePageChange(pageNum)}
                      className="w-8 h-8 p-0"
                    >
                      {pageNum}
                    </Button>
                  );
                })}
              </div>

              <Button
                variant="outline"
                size="sm"
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={!pagination.hasNextPage}
              >
                Next
                <ChevronRight className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

function CustomerEditForm({ 
  user, 
  onUpdate, 
  onBalanceAction 
}: { 
  user: User; 
  onUpdate: (updates: Partial<User>) => void;
  onBalanceAction: (action: string, amount: string) => void;
}) {
  const [formData, setFormData] = useState({
    reputation: user.reputation,
    winLoseSetting: user.winLoseSetting,
    direction: user.direction,
    isBanned: user.isBanned,
    withdrawalProhibited: user.withdrawalProhibited,
    tasksBan: user.tasksBan || "Allowed",
  });

  const [balanceActions, setBalanceActions] = useState({
    deposit: "",
    deduct: "",
    freeze: "",
    unfreeze: "",
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onUpdate(formData);
  };

  return (
    <div className="space-y-6">
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <Label htmlFor="reputation">Reputation (0-100)</Label>
            <Input
              id="reputation"
              type="number"
              min="0"
              max="100"
              value={formData.reputation}
              onChange={(e) => setFormData({ ...formData, reputation: parseInt(e.target.value) })}
            />
          </div>
          <div>
            <Label htmlFor="winLose">Win/Lose Setting</Label>
            <Select value={formData.winLoseSetting} onValueChange={(value) => setFormData({ ...formData, winLoseSetting: value })}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="To Win">To Win</SelectItem>
                <SelectItem value="To Lose">To Lose</SelectItem>
                <SelectItem value="Random">Random</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div>
            <Label htmlFor="direction">Direction</Label>
            <Select value={formData.direction} onValueChange={(value) => setFormData({ ...formData, direction: value })}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="Buy Up">Buy Up</SelectItem>
                <SelectItem value="Buy the Dip">Buy the Dip</SelectItem>
                <SelectItem value="Actual">Actual</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div>
            <Label htmlFor="accountStatus">Account Status</Label>
            <Select value={formData.isBanned ? "Banned" : "Active"} onValueChange={(value) => setFormData({ ...formData, isBanned: value === "Banned" })}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="Active">Active</SelectItem>
                <SelectItem value="Banned">Banned</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div>
            <Label htmlFor="withdrawalStatus">Withdrawal Status</Label>
            <Select value={formData.withdrawalProhibited ? "Prohibited" : "Allowed"} onValueChange={(value) => setFormData({ ...formData, withdrawalProhibited: value === "Prohibited" })}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="Allowed">Allowed</SelectItem>
                <SelectItem value="Prohibited">Prohibited</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div>
            <Label htmlFor="tasksBan">Tasks Ban</Label>
            <Select value={formData.tasksBan} onValueChange={(value) => setFormData({ ...formData, tasksBan: value })}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="Allowed">Allowed</SelectItem>
                <SelectItem value="Prohibit">Prohibit</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        <Button type="submit" className="w-full">Save Changes</Button>
      </form>

      <div className="border-t pt-4">
        <h4 className="font-medium mb-3">Balance Actions</h4>
        <div className="grid grid-cols-2 gap-3">
          <div>
            <Label>Deposit</Label>
            <div className="flex">
              <Input
                type="number"
                placeholder="Amount"
                value={balanceActions.deposit}
                onChange={(e) => setBalanceActions({ ...balanceActions, deposit: e.target.value })}
                className="rounded-r-none"
              />
              <Button
                type="button"
                onClick={() => {
                  onBalanceAction("deposit", balanceActions.deposit);
                  setBalanceActions({ ...balanceActions, deposit: "" });
                }}
                className="rounded-l-none bg-success hover:bg-success/90"
              >
                <Plus className="w-4 h-4" />
              </Button>
            </div>
          </div>
          <div>
            <Label>Deduct</Label>
            <div className="flex">
              <Input
                type="number"
                placeholder="Amount"
                value={balanceActions.deduct}
                onChange={(e) => setBalanceActions({ ...balanceActions, deduct: e.target.value })}
                className="rounded-r-none"
              />
              <Button
                type="button"
                onClick={() => {
                  onBalanceAction("withdrawal", balanceActions.deduct);
                  setBalanceActions({ ...balanceActions, deduct: "" });
                }}
                className="rounded-l-none bg-destructive hover:bg-destructive/90"
              >
                <Minus className="w-4 h-4" />
              </Button>
            </div>
          </div>
          <div>
            <Label>Freeze</Label>
            <div className="flex">
              <Input
                type="number"
                placeholder="Amount"
                value={balanceActions.freeze}
                onChange={(e) => setBalanceActions({ ...balanceActions, freeze: e.target.value })}
                className="rounded-r-none"
              />
              <Button
                type="button"
                onClick={() => {
                  onBalanceAction("freeze", balanceActions.freeze);
                  setBalanceActions({ ...balanceActions, freeze: "" });
                }}
                className="rounded-l-none bg-warning hover:bg-warning/90"
              >
                <Lock className="w-4 h-4" />
              </Button>
            </div>
          </div>
          <div>
            <Label>Unfreeze</Label>
            <div className="flex">
              <Input
                type="number"
                placeholder="Amount"
                value={balanceActions.unfreeze}
                onChange={(e) => setBalanceActions({ ...balanceActions, unfreeze: e.target.value })}
                className="rounded-r-none"
              />
              <Button
                type="button"
                onClick={() => {
                  onBalanceAction("unfreeze", balanceActions.unfreeze);
                  setBalanceActions({ ...balanceActions, unfreeze: "" });
                }}
                className="rounded-l-none bg-primary hover:bg-primary/90"
              >
                <LockOpen className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

