                        <div className="mt-2 text-xs text-gray-500">
                          Apply Time: {new Date(withdrawal.createdAt).toLocaleString('en-CA', {
                            year: 'numeric',
                            month: '2-digit', 
                            day: '2-digit',
                            hour: '2-digit',
                            minute: '2-digit',
                            second: '2-digit',
                            hour12: false
                          }).replace(',', '')}
