      onSuccess: () => {
        setSignatureData(null);
        setSignatureName('');
        clearSignature();

        // Force refresh user data to show removed signature
        queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });

        toast({
          title: "Success", 
          description: "Signature deleted successfully!"
        });
      },
      onError: () => {
